export async function GET() {
  try {
    console.log('ğŸ”§ Starting data restoration...')
    
    // Check if already seeded (has positions)
    const existingPositions = await prisma.position.count()
    if (existingPositions > 10) {
      return NextResponse.json({ 
        message: 'Database already has data',
        positions: existingPositions 
      })
    }

    // 1. Create/Update Admin user
    const adminPassword = await bcrypt.hash('Admin123!', 10)
    await prisma.user.upsert({
      where: { email: 'admin@twenty2.co.il' },
      update: {},
      create: {
        email: 'admin@twenty2.co.il',
        name: '×× ×”×œ ××¢×¨×›×ª',
        password: adminPassword,
        role: 'ADMIN',
        active: true,
      },
    })
    console.log('âœ… Admin user created')

    // 2. Create tags
    for (const tag of TAGS) {
      await prisma.tag.upsert({
        where: { name: tag.name },
        update: tag,
        create: tag,
      })
    }
    console.log(`âœ… ${TAGS.length} tags created`)

    // 3. Create departments
    for (const dept of DEPARTMENTS) {
      await prisma.department.upsert({
        where: { code: dept.code },
        update: dept,
        create: dept,
      })
    }
    console.log(`âœ… ${DEPARTMENTS.length} departments created`)

    // 4. Create employers
    for (const employer of EMPLOYERS) {
      await prisma.employer.upsert({
        where: { email: employer.email },
        update: employer,
        create: employer,
      })
    }
    console.log(`âœ… ${EMPLOYERS.length} employers created`)

    // 5. Create positions
    let totalPositions = 0
    for (const [employerName, positions] of Object.entries(POSITIONS)) {
      const employer = await prisma.employer.findFirst({
        where: { name: employerName }
      })
      
      if (!employer) {
        console.log(`âš ï¸ Employer not found: ${employerName}`)
        continue
      }

      for (const position of positions) {
        try {
          await prisma.position.create({
            data: {
              ...position,
              employerId: employer.id,
              status: 'ACTIVE'
            },
          })
          totalPositions++
        } catch (e) {
          // Position might already exist, skip
        }
      }
    }
    console.log(`âœ… ${totalPositions} positions created`)

    // Get final counts
    const stats = {
      users: await prisma.user.count(),
      employers: await prisma.employer.count(),
      positions: await prisma.position.count(),
      tags: await prisma.tag.count(),
      departments: await prisma.department.count(),
    }

    return NextResponse.json({ 
      success: true,
      message: 'Data restored successfully!',
      stats
    })

  } catch (error) {
    console.error('âŒ Restoration error:', error)
    return NextResponse.json({ 
      error: 'Restoration failed',
      details: error instanceof Error ? error.message : 'Unknown error'
    }, { status: 500 })
  }
}
